# Test are copied from https://github.com/TheCharlatan/rust-bitcoinkernel/blob/e91264a877576297f4345d68196c15e0a2a5846e/tests/test.rs#L332-L374


import pbk

import pytest


def _test_verify_script(
    script_pubkey_hex: str, amount: int, tx_to_hex: str, input_index: int
) -> bool:
    script_pubkey = pbk.ScriptPubkey(bytes.fromhex(script_pubkey_hex))
    tx_to = pbk.Transaction(bytes.fromhex(tx_to_hex))
    return pbk.verify_script(
        script_pubkey,
        amount,
        tx_to,
        None,
        input_index,
        pbk.ScriptFlags.VERIFY_ALL - pbk.ScriptFlags.VERIFY_TAPROOT,
    )


def test_valid_scripts():
    # a random old-style transaction from the blockchain
    assert _test_verify_script(
        "76a9144bfbaf6afb76cc5771bc6404810d1cc041a6933988ac",
        0,
        "",
        0,
    )

    # a random segwit transaction from the blockchain using P2SH
    assert _test_verify_script(
        "a91434c06f8c87e355e123bdc6dda4ffabc64b6989ef87",
        1900000,
        "",
        0,
    )
    # a random segwit transaction from the blockchain using native segwit
    assert _test_verify_script(
        "0020701a8d401c84fb13e6baf169d59684e17abd9fa216c8cc5b9fc63d622ff8c58d",
        18393430,
        "",
        0,
    )


def test_invalid_scripts():
    # a random old-style transaction from the blockchain - WITH WRONG SIGNATURE for the address
    with pytest.raises(pbk.ScriptVerifyException):
        _test_verify_script(
            "76a9144bfbaf6afb76cc5771bc6404810d1cc041a6933988ff",
            0,
            "",
            0,
        )

    # a random segwit transaction from the blockchain using P2SH - WITH WRONG AMOUNT
    with pytest.raises(pbk.ScriptVerifyException):
        _test_verify_script(
            "a91434c06f8c87e355e123bdc6dda4ffabc64b6989ef87",
            900000,
            "",
            0,
        )

    # a random segwit transaction from the blockchain using native segwit - WITH WRONG SEGWIT
    with pytest.raises(pbk.ScriptVerifyException):
        _test_verify_script(
            "0020701a8d401c84fb13e6baf169d59684e17abd9fa216c8cc5b9fc63d622ff8c58f",
            18393430,
            "",
            0,
        )
